# CAMINHO PERCORRIDO POR CADA ALUNO
CREATE OR REPLACE VIEW CAMINHO AS SELECT AL.ID_ALUNO,AL.NOME,AT.DESC_ATIVIDADE,N.NOTA,N.DATA_INICIO,N.DATA_FIM 
FROM ATIVIDADE AT, NOS N, ALUNO AL
WHERE  AT.ID_ATIVIDADE = N.ID_ATIVIDADE AND
	   AL.ID_ALUNO = N.ID_ALUNO
ORDER BY N.ID_ALUNO, N.ORDEM;
